import Express, {Response, Request} from "express";
import base64EncodeUnicode, { getTokenId, importFont } from "../core/utils";
import { env } from "../core/config";
import { createCanvas, registerFont } from "canvas";
const sharp = require("sharp")

const fs = require("fs");
const router = Express.Router();
const path = require('path');

const fontDir =  path.join((env.NODE_ENV === 'development' ? 'src' : 'build'), 'assets/font');
const satoshiFont = path.join(fontDir, "Satoshi-Bold.ttf");
const notoFont = path.join(fontDir, "NotoColorEmoji.ttf");
const appleColor = path.join(fontDir, "AppleColorEmoji.ttc");

const satoshiFontUrl = importFont(satoshiFont, 'font/truetype'); 
const notoFontUrl = importFont(notoFont, 'font/truetype'); 

router.get("/", async (req: Request, res: Response) => {
    const name = req.query.name?.toString() || "";
    const type = req.query.type?.toString() || "jpeg";
    let width = parseInt(req.query.width?.toString() || "1200");
    if(width > 1200) width = 1200; 

    const base64 = base64EncodeUnicode(createCardSvg(name, (type == "jpeg"))) 
    const buffer = Buffer.from(base64, 'base64');
  
    if(type == "jpeg") {
      const jpeg = await sharp(buffer, ).jpeg({ quality: 100, progressive: true }).toBuffer()
      res
          .writeHead(200, {
          'Content-Type': 'image/webp',
          'Content-Length': jpeg.length,
          }).end(jpeg); 
    } else {
      res
          .writeHead(200, {
          'Content-Type': 'image/svg+xml',
          'Content-Length': buffer.length,
          }).end(buffer);
    }
});
 
export const obscureName = (name: string, len: number) => {
    if(getLength(name) > len) {
        return Array.from(name).slice(0, len / 2).join("") + "..." + Array.from(name).slice(name.length - (len / 2), name.length).join("");
    } else {
        return name;
    }
}

export const getLength = (label: string) => { 
    return Array.from(label).length;
}

